#' }
#' 
#' Set \code{options(googleAuthR.scopes.selected = c(https://www.googleapis.com/auth/bigquery, https://www.googleapis.com/auth/cloud-platform, https://www.googleapis.com/auth/cloud-platform.read-only)}
#' Then run \code{googleAuthR::gar_auth()} to authenticate.
#' See \code{\link[googleAuthR]{gar_auth}} for details. 
#' 
#' @param projectId Project ID of the tables to list
#' @param datasetId Dataset ID of the tables to list
#' @param maxResults Maximum number of results to return
#' @param pageToken Page token, returned by a previous call, to request the next page of results
#' @importFrom googleAuthR gar_api_generator
#' @export
tables.list <- function(projectId, datasetId, maxResults = NULL, pageToken = NULL) {
    url <- sprintf("https://www.googleapis.com/bigquery/v2/projects/%s/datasets/%s/tables", 
        projectId, datasetId)
    # bigquery.tables.list
    pars = list(maxResults = maxResults, pageToken = pageToken)
    f <- googleAuthR::gar_api_generator(url, "GET", pars_args = rmNullObs(pars), 
        data_parse_function = function(x) x)
    f()
    
}

