WatchRequest <- function(labelFilterAction = NULL, labelIds = NULL, topicName = NULL) {
    structure(list(labelFilterAction = labelFilterAction, labelIds = labelIds, topicName = topicName), 
        class = "gar_WatchRequest")
}


#' WatchResponse Object
