#' \item https://www.googleapis.com/auth/youtube.force-ssl
#' \item https://www.googleapis.com/auth/youtubepartner
#' }
#' 
#' Set \code{options(googleAuthR.scopes.selected = c(https://www.googleapis.com/auth/youtube, https://www.googleapis.com/auth/youtube.force-ssl, https://www.googleapis.com/auth/youtubepartner)}
#' Then run \code{googleAuthR::gar_auth()} to authenticate.
#' See \code{\link[googleAuthR]{gar_auth}} for details. 
#' 
#' @param channelId The channelId parameter specifies the YouTube channel ID for which the watermark is being unset

#' @param onBehalfOfContentOwner Note: This parameter is intended exclusively for YouTube content partners
#' @importFrom googleAuthR gar_api_generator
#' @export


watermarks.unset <- function(channelId, onBehalfOfContentOwner = NULL) {
    
    
    url <- "https://www.googleapis.com/youtube/v3/watermarks/unset"
    # youtube.watermarks.unset
    pars = list(channelId = channelId, onBehalfOfContentOwner = onBehalfOfContentOwner)
    
    f <- googleAuthR::gar_api_generator(url, "POST", pars_args = rmNullObs(pars), 
        data_parse_function = function(x) x)
    
    f()
    
    
}



